style.css:
@-moz-document domain("cloud.feedly.com"), domain("feedly.com") {
*,
.u4Entry .title, .u12Entry .title, .u5Entry .title, .u5Entry.textOnly .title, .u100Entry .title, #feedlyTabs .label, .fx .entry .title, * .title {
  font-family: 'Iranian Sans'
}

.title , .metadata, .entryBody {
	direction:rtl;
}

.u4Entry .metadata {
	direction:ltr;
}

.summary {
  padding-left:0 !important;
  -moz-padding-start: 7px !important;
  -webkit-padding-start: 7px !important;
	direction:rtl;
    text-align:justify;
}

.title {
  text-align:right;  
}
}
