style.css:
@-moz-document 
    domain("2ch.hk"), 
    domain("2ch.pm"), 
    domain("2ch.re"), 
    domain("2ch.tf"), 
    domain("2ch.wf"), 
    domain("2ch.yt"), 
    domain("2-ch.so") {

    .posts .post-wrapper, .posts .oppost-wrapper,
    .thread .oppost, .thread .post {
        width: 100% !important;
        clear: both !important;
    }

    .posts, form > .thread {
        width: /*[[posts_width]]*/px !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }

/*[[hide_rek]]*/
}
