style.css:
@-moz-document domain("stackoverflow.com"), domain("answers.onstartups.com"), domain("superuser.com"), domain("askubuntu.com"), domain("serverfault.com"), domain("mathoverflow.net"), domain("stackapps.com"), regexp("^https?://([a-z\\-]+\\.)?stackexchange\\.com.+") {
.topbar {
    background-color: rgb(220,220,220) !important;
}
.topbar .topbar-links .topbar-flair .reputation,
.topbar .topbar-links .topbar-flair .badgecount,
.topbar .topbar-links .topbar-menu-links a,
.topbar .topbar-links .topbar-menu-links a:visited {
    color: rgb(120, 120, 120) !important;
}
.topbar .topbar-links .topbar-menu-links a:hover,
.topbar .topbar-icon:hover,
.topbar .topbar-links .profile-me:hover {
	background-color: rgb(200, 200, 200) !important;
}
.topbar .topbar-links .topbar-menu-links a.topbar-icon-on,
.topbar .topbar-links .topbar-menu-links a.topbar-icon-on:hover,
.topbar .topbar-icon-on,
.topbar .topbar-icon-on:hover {
    background-color: rgb(240, 240, 240) !important;
}
.topbar {
	position: fixed;
	top: 0;
	z-index: 249;
}
.container {
    margin-top: 34px;
}
}
