style.css:
@-moz-document domain("duckduckgo.com") {
  
  .header {
    color: #555555;
  }

  .query {
    border-color: rgb(164, 167, 182);
    border-radius: 0px;
  }

  .link-text {
    color: rgb(0, 102, 33);
  }

  .navbutton {
    color: rgb(26, 13, 171);
  }

  a {
    color: rgb(26, 13, 171);
  }

  .result-snippet {
    font-size: 80%;
    color: rgb(60, 60, 60);
  }

}
