style.css:
@-moz-document domain("grupia.com")
{
	.header-container, .header
	{
		background-color: #080F3A;
		background-image: linear-gradient(to bottom right, #313A75 0%, #080F3A 100%) !important;
	}
	.button.highlight, .side-menu-button.selected, .context-menu.show a:hover
	{
		background-color: #313A75 !important;
		background-image: radial-gradient(ellipse farthest-corner at center top, #525B92 0%, #313A75 100%) !important;
	}
	.side-menu-button.selected
	{
		color: #FFFFFF !important;
	}

	.comunity-title, .user-profile-dashboard h1
	{
		color: #313A75;
	}

	.soft-card
	{
		color: black;
	}

	.card-text a
	{
		color: #313A75;
	}
}
