style.css:
@-moz-document domain("techhive.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("techhive.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

#amazon-bottom-widget .amzn-custom-header,
#home-main-primary h2,
#th-nav,
.subCategories strong,
.subCategories ul li {
	text-transform: capitalize!important;
}

#indexLeadItems .content-image img,
.home-feature a:hover,
.home-features-inner .slick-list,
.pag-next,
.pagination-inner
{
	border: none!important;
	box-shadow: none!important;
}

.excerpt-text {
	float: left!important;
}

/*___ Fonts ________________________________________________________________________*/

#home-main-primary .excerpt p.crawl-headline a,
.native-author .excerpt p.crawl-headline a,
.resources .river-well h3,
.subCategories strong {
	font-size: 23px!important;
}

.resources .river-well div.summary,
.resources .river-well h4,
.resources .river-well ol,
.resources .river-well p,
.resources .river-well ul,
.subCategories ul li,
body {
	font-size: 19px!important;
}

article .related-promo-wrapper .sector ul li .item-eyebrow {
	font-size: 15px!important;
	line-height: 27px!important;
}

#drr-container.cat>ol,
#drr-container.cat>p,
#drr-container.cat>ul
{
    font-size: 1.25rem;
    line-height: 1.95rem;
}

/*___ Remove Items _________________________________________________________________*/

#gpt-showcase.home-features-ad,
#homepageLeaderboardWrapper,
.side-ad,
.side-promo
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.home-features-carousel {
	margin-left: 1px!important;
}

body #main #inner-features {
    margin-left: -47px!important;
}

.excerpt-text {
	margin-left: 2rem!important;
}

.byline p.dateline {
	margin-left: 7rem!important;
}

#main .tocWrapper.attached,
.article-product-hub .tocWrapper.attached {
    left: 76.8438px!important;
}

#home-main-primary .excerpt {
    margin-bottom: 6px;
    padding-bottom: 6px;
}

/*___ Margins ______________________________________________________________________*/

#main,
#side,
.container,
.home-features-wrap,
.home-hero,
article,
article .epo.cat-narrow {
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

.article-product-hub .product-hub-hdr {
    margin: 0!important;
}

.article-product-hub #ciu,
.article-product-hub .product-hub-content {
    margin: 0 13rem!important;
}

#indexLeadItems {
    margin: 1rem 0!important;
}

#drr-container.cat {
    margin: 0 0 16px -27rem;
}

/*___ Paddings _____________________________________________________________________*/

#main,
#side,
.container,
.home-hero,
article,
article .epo.cat-narrow {
	padding: 12px!important;
}


/*___ Heights ______________________________________________________________________*/

.home-hero,
[class*="imageContainer"] {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

#drr-container.cat blockquote,
#drr-container.cat figure.full,
#drr-container.cat figure.large,
#drr-container.cat h2,
#drr-container.cat h3,
#drr-container.cat h4,
#drr-container.cat h5,
#drr-container.cat li,
#drr-container.cat ol,
#drr-container.cat ul,
#drr-container.cat,
#drr-container.cat>p,
#main,
.article-product-hub #ciu,
.article-product-hub .product-hub-content,
.article-product-hub .product-hub-hdr,
.article-product-hub .product-hub-left,
.home #main,
.product-hub-articleBody blockquote,
.product-hub-articleBody figure.full,
.product-hub-articleBody figure.large,
.product-hub-articleBody h2,
.product-hub-articleBody h3,
.product-hub-articleBody h4,
.product-hub-articleBody h5,
.product-hub-articleBody li,
.product-hub-articleBody ol,
.product-hub-articleBody ul,
.product-hub-articleBody>p,
article,
article .constrain-970,
article header.cat #articleLeaderboardWrapper,
article header.cat .byline,
article header.cat .cat-social,
article header.cat .category,
article header.cat .deck,
article header.cat h1,
article header.cat nav.breadcrumbs
{
	max-width: none!important;
}

.home-features-carousel,
body #main #inner-features {
	width: 100%!important;
}

.container,
.home-features,
.home-hero,
body #main .home-features-inner
{
	width: 98%!important;
}

.excerpt-text {
	width: 82%!important;
}

#main,
.article-product-hub #ciu,
.article-product-hub .product-hub-content,
.article-product-hub .product-hub-hdr,
.home #main {
	width: 81%!important;
}

.article-product-hub .product-hub-left {
    width: 65%!important;
}

article .epo.cat-narrow {
	width: 18%!important;
}
}

@-moz-document domain("techhive.com") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.latest-reviews ul li:hover h5,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#home-main-primary .excerpt-text,
.latest-reviews ul li h5,
button,
html,
input,
li,
p,
select,
textarea {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

#amazon-bottom-widget .amzn-custom-header,
#main .tocWrapper legend,
.article-product-hub .tocWrapper legend,
.home-features-carousel .slick-slide:hover p,
.latest-reviews .hed,
.latest-reviews h2,
.subCategories strong,
article .related-promo-wrapper .sector .hed,
article .related-promo-wrapper .sector ul li .item-eyebrow,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

p strong,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.article-product-hub .related,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

#related-product-hubs ul p,
.cat.byline .dateline .pub-date,
.timeago,
em strong,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.at-a-glance .price-msrp a,
.emo.emo-sb .msrp a,
.latest-reviews ul li>span a.product-price {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

}

@-moz-document domain("techhive.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.home-hero-text.lightTheme {
	background: rgba(0, 0, 0, .39)!important;
	
}

