style.css:
@-moz-document domain("huyosoku.com") {
  iframe,
  #header,
  #pankuzu,
  #header-feed,
  div.ad,
  .original_thread,
  div.social,
  div.meta,
  #pickup-feed,
  #sub-column,
  div.f-ad,
  #relatedpost,
  #popularpost,
  div.res-quote,
  #a-pickup-feed,
  #cat-post,
  #comment,
  #footer,
  #fixed-affiliate,
  div.res_content > br
  {
      display: none;
  }

  div.res_content {
    margin-bottom: 20px;
  }
  
  #contents {
      width: 800px;
  }

  * {
      font-size: 12px !important;
      font-weight: normal !important;
  }
}
