style.css:
@-moz-document domain("hadoop.apache.org") {
#leftColumn {
    width:260px;
}
#bodyColumn {
    margin-left:287px;
}
#navcolumn ul a, td, select, input, li {
    color : #000;
}
#navcolumn li.none {
    margin-bottom: 0.1em;
}
table.bodyTable th {
    background-color: #333;
}
table.bodyTable th, table.bodyTable td {
    font-size: 16px;
    padding: 10px;
}
h3, h2 {
    color: #111;
}

.source {
    border:none;
    margin: 0;
    padding:0;
}
pre {
    background: #f3f3f3;
    padding:5px;
}
}
