style.css:
@-moz-document domain("uflix.me") {
  .form-control {border-radius: 0px;}
  .thumbbb {border-radius: 0px; color: #8C59D5; }
  .visible-lg {display: none !important;}
  .alert-info, .btn-info {display: none;}
  label {background-color: #8E8FFF; padding: 5px; border: 1px solid #0015FF; color: #FFF;}
  a.thumbbb:hover {background: #7032BC none repeat scroll 0% 0%; transition: background-color 0.40s;}
  .browse .form-group, .user-profile .form-group {margin-right: 0px; margin-left: 14px;}
  .form-control {border: 1px solid rgb(138, 66, 204);}
}
