style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("simtropolis.com") {


    
    .img-circle {
        border-radius: 25%;
    }
    
    .ipsUserPhoto{
      background:#fff;
      vertical-align:middle;
      display:inline-block;
      line-height:1px;
      position:relative;
      margin:0px;
      border-radius:25%;
    }

    .ipsUserPhoto:after{
      content:'';
      display:block;
      position:absolute;
      left:-1px;
      top:-1px;
      right:-1px;
      bottom:-1px;
      width:auto !important;
      height:auto !important;
      border:1px solid #fff;
      box-shadow:0px 0px 0px 1px #e2e2e2;
    }

    .ipsUserPhoto_tiny img, img.ipsUserPhoto_tiny, .ipsUserPhoto_tiny:after{
      width:34px;
      height:34px;
      border-radius:25%;
    }

    .ipsUserPhoto_mini img, img.ipsUserPhoto_mini, .ipsUserPhoto_mini:after{
      width:44px;
      height:44px;
      border-radius:25%;
    }

    .ipsUserPhoto_small img, img.ipsUserPhoto_small, .ipsUserPhoto_small:after{
      width:54px;
      height:54px;
      border-radius:25%;
    }

    .ipsUserPhoto_medium img, img.ipsUserPhoto_medium, .ipsUserPhoto_medium:after{
      width:75px;
      height:75px;
      border-radius:25%;
    }

    .ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large:after{
      width:90px;
      height:90px;
      border-radius:25%;
    }

    .ipsUserPhoto_xlarge img, img.ipsUserPhoto_xlarge, .ipsUserPhoto_xlarge:after{
      width:120px;
      height:120px;
      border-radius:25%;
    }

    .ipsPhotoPanel > .ipsUserPhoto{
      float:left;
      line-height:0;
    }
}
