style.css:
@-moz-document regexp('.*tieba.baidu.com/(f\?.*w=)?chrome(&.*)?.*') {
.card_banner img, .card_head img {
    display:none
}
.card_banner {
    background-image:url('http://imgsrc.baidu.com/forum/pic/item/95910bd5ad6eddc48151d6fd3fdbb6fd536633e3.jpg');
    background-repeat:no-repeat;
    background-size:cover;
    background-position:top right;
}
.card_head a {
    background-image:url('http://imgsrc.baidu.com/forum/pic/item/4e5ca6003af33a87bfea621cc05c10385243b592.jpg');
    background-repeat:no-repeat;
    background-size:cover;
    background-position:top right;
    display: block;
    width: 150px;
    height: 150px;
}
}
