style.css:
@-moz-document domain("isbullsh.it") {
   
    body.post{
       width: 90% !important;
       margin: 0 auto !important;
       padding: 0 !important; 
       font-size: 16px !important;
       line-height: 20px !important;
       color:#222 !important;
    }

    body.post header > div.post-data{
            right: 5px !important;
    }
    body.post div.row > aside.span3{
        display:none !important;
    }
    body.post div.span9 {
        display: block !important;
        margin: 0 !important;
        width: 100% !important;
        padding: 0 !important;
        float:none !important;
    }
    body.post > div.container > div.row{
        position:static !important;
        margin-left:0 !important;

    }
    body.post > div.container{
        display: block !important;
        width: 100% !important;
        margin: 0 !important;
    }
    body.post div[role=main] article{
        padding: 10px 22px !important;
    }

}
