@-moz-document regexp("http(s)?://forum\.gamer\.com\.tw/(A|B|B_legend|Bo|C|C_legend|Co|G1|G2|rules|water|search)\.php.*"){
    /*banner*/#BH-bigbanner{visibility:collapse!important;height:0px!important;}
    /*banner*/#BH-bigbanner *{height:1px!important;overflow:hidden!important;}
    /*right object-1 A*/#BH-slave{height:initial!important;min-height:initial!important;}
    /*right object-1 C*/
    #BH-slave>.flySalve,
    #BH-slave *::after{
        max-height:1px!important;color:#000!important;background:none #000!important;position:static!important;
        margin:0px!important;padding:0px!important;font-size:0px!important;line-height:0px!important;}
    #BH-slave>.BH-rbox.FM-rbox14,
    #BH-slave>.c-fixed--header,
    #BH-slave>.c-fixed--footer{
        display:none!important;}
    
    
    /*resize*/#BH-master,.FM-abox1{margin:auto!important;float:unset!important;}
    /*resize*/#BH-master{width:900px!important;}
    /*main - collapse*/#BH-wrapper{margin-top:0px!important;}
    
    
    /*board menu - fix*/#BH-menu-fill.zero{height:0px!important;display:initial!important;}
    /*board menu - fix*/#BH-menu-path.BH-menu.fixed{width:initial!important;}
    /*board menu - BG*/        ul.BH-menuE{background:#030!important;margin:auto!important;}
    /*board menu - popup*/     ul.BH-menuE dl{background:#030!important;}
    /*board menu - first*/     ul.BH-menuE li:first-child>a{background:#117E96!important;color:#fff!important;}
    /*board menu - activating*/ul.BH-menuE .now{background:#050!important;}
    /*board menu - separator*/ ul.BH-menuE li>a::before{visibility:collapse!important;}
    /*board menu - collapse*/.BH-menu{padding:0px!important;}
    
    /*recommend post - BG*/.BH-rbox{background:#000!important;}
    /*recommend post - hover*/.BH-rbox>p>a:hover{background:#030!important;}
    /*thread tag - activating*/#FM-tagsnow{background:#050!important;color:#fff!important;}
    /*thread tag - BG*/.FM-tags a{background:none #333!important;color:#faa!important;font-size:18px!important;padding:4px!important;margin-right:0px!important;width:unset!important;}
    
    /*pages*/    /*pages*/    /*pages*/
    #BH-pagebtn{padding:30px 0px!important;}
    #BH-pagebtn>a{font:30px/100% arial!important;color:#fff!important;padding:2px 30px!important;margin:5px auto 0px!important;background:#080!important;}
    #BH-pagebtn>a:hover{background:#a51!important;}
    #BH-pagebtn>.BH-pagebtnA  {font:40px/100% arial!important;margin:0 auto!important;}
    #BH-pagebtn>.BH-pagebtnA>a{font:40px/100% arial!important;color:#fff!important;background:#080!important;padding:2px 5px!important;margin:0px 5px!important;}
    #BH-pagebtn>.BH-pagebtnA>a.pagenow,
    #BH-pagebtn>.BH-pagebtnA>a:hover{background:#a51!important;} 
}
