style.css:
@-moz-document domain("apps.evozi.com") {
  .col-lg-12, .panel-heading,
  .panel-default,
  form, .hreview-aggregate              {display: none}
  .panel                                {border-radius: 0px; box-shadow: none; border: 1px solid #DDD; margin-top: 50px;}
  .btn, .form-control                   {border-radius: 0px; box-shadow: none; transition-duration: 0.3s;}
  .form-control:focus                   {box-shadow: none;}
  .dropdown-toggle                      {display: none}
}
