style.css:
@-moz-document url("http://www.bing.com/") {
#sc_closer,#sb_foot{
display:none !important;
}
  
  /*search bar*/
#sbox {
  position: fixed !important;
  top: 40% !important;
  left: 0 !important;
  width: 100% !important;
  text-align: center;
  margin: 2em;
}
#sb_form {
transition: opacity 1s !important;
opacity: .0 !important;  
}
#sb_form:hover{
transition: opacity 1s !important;
opacity: .9 !important;  
}
.hp_sw_logo  {
    position: fixed !important;
    top: calc(40% - 30px);
    left: calc(50% - 66px);
    opacity: 0.9 !important;
}
 .sa_as {
    display: block !important;
    height: 0;
    transition: height 0.25s;
  }
  .sa_as[style="display: block;"] {
    height: 283px;
  }
  
  
  
  

#hp_sw_hdr{
  transition: opacity 1s !important;
  opacity: .0 !important; 
  
}
#hp_sw_hdr:hover{
  opacity: .9 !important;
}
#musCard{
  transition: opacity 1s !important;
  opacity: .0 !important;
}
#musCard:hover{
  transition: opacity 1s !important;
  opacity: .9 !important; 
}  
#bgDiv {
  position: fixed;
  top: 0 !important;
  left: 0 !important;
  height: 100% !important;
  width: 100% !important;
  max-width: none;
  max-height: none;
  animation: myfirst 1s;
}
.sa_n .sa_s  {
    width: 100%;
  }
  #sb_form_q {
    text-align: center;
  }
}
