style.css:
@-moz-document url-prefix("https://www.khronos.org/message_boards/showthread.php"), url-prefix("https://forums.khronos.org/showthread.php") {
.postlist {
  max-width: 70em;
  margin-left: auto;
  margin-right: auto;
}


.postrow .content 
{
    font-size: 110%;
    max-width: 42em;
}







body > div.body_wrapper > div.navlinks:after
{
    display: block;
    margin-top: 1em;
    margin-left: auto;
    margin-right: auto;

    color: #a41e22;
    font-weight: bold;
    max-width: 43em;
    text-shadow: 1px 1px 2px rgba(150, 150, 150, 1); 
    content: " If you want to get started quickly with Vulkan, you can find a simple library to open a window and run a default swapchain on Linux or Windows at krust.org."
}
}

@-moz-document url-prefix("https://forums.khronos.org/forumdisplay.php") {

body > div.body_wrapper
{
    max-width: 80em;
    margin-left: auto;
    margin-right: auto;
}

}

@-moz-document regexp("https://forums.khronos.org/forumdisplay.php/.*(v|V)ulkan.*") {
#navbar_notice_1:after
{
    content: " If you want to get started quickly with Vulkan, you can find a simple library to open a window and run a default swapchain on Linux or Windows at krust.org."
}
}
