body {    background-color: #303738 !Important; color: #EAEAEA !Important;}
.well {    background-color: #1C2021 !important;     border: 1px solid #1C2021 !Important; box-shadow: 0px 3px 2px #101C1F !important;}
.alert-info {background-color: #1C2021 !important;     border: 1px solid #1C2021 !Important; box-shadow: 0px 3px 2px #101C1F !important; color: #EAEAEA !Important;}
