.live-skin-coloration-area .live-skin-highlight-bg {background-color: rgb(170, 170, 0)!important;}
.recharge-store-box dl.recharge-form dt,.live-skin-coloration-area .live-skin-normal-a-text:link, .live-skin-normal-a-text:visited,.live-skin-coloration-area .live-skin-normal-a-text,.live-skin-coloration-area .live-skin-normal-text { color: #eee !important }
body { color: #aaa }
span[style="color: rgb(0, 0, 0);"] { color: rgb(132, 181, 255) !important }
}

@-moz-document url-prefix("https://member.bilibili.com/"), domain("search.bilibili.com") {
#app, body {
	background: #111 !important; }
}

@-moz-document url-prefix("https://www.bilibili.com/blackboard/") {
.module-information .video-player, .module-information .video-list .video-item, .award-list, .module-reserve .block-follow, .module-game .rule { background: #222 !important }
.module-information .video-list .video-item .name { color: #eb4343 !important }
}

@-moz-document domain("message.bilibili.com") {
.active, .live-container { background-color: #222 !important; }
}

@-moz-document url-prefix("https://www.bilibili.com/bangumi/"), url-prefix("https://www.bilibili.com/video/") {
a{ color: #4eb7d9 !important; }
.list-item .name {    color: #ccc !important;}
}
