style.css:
@-moz-document domain("heraldk.com"), domain("heraldcorp.com") {
  #sidebar, #content > .row, .main_cate_best 
  {
    display: none !important;
  }

  #primary {
    float: none !important;
    margin: 0 auto !important;
    border: none !important;
  }

  .article-body {
    background-image: none !important;
  }

  #content, .news_text, .article {
    font-family: "KoPubBatang", "나눔명조", "맑은 고딕", "나눔바른고딕", "나눔고딕" !important;
    line-height: 1.85em !important;
  }

  p {
    font-size: 1.3em !important ;
    line-height: 1.85em !important;
  }
}
