style.css:
@-moz-document regexp(".*") {

head, link[href="resource://gre/res/ImageDocument.css"] {display: inline}
link[href="resource://gre/res/ImageDocument.css"]::before {
  content: "";
  position: fixed;
  top: 0; bottom: 0;
  left: 0; right: 0;
  background: url(/*[[image]]*/) fixed center center/*[[size]]*/ /*[[color]]*/;
}

}
