style.css:
@-moz-document domain("playcatan.com") {
nav a {
    height: 32px;
    line-height: 28px;
}
.smallscreen header h1 {
    height: 22px;
}
.smallscreen header {
    height: 32px;
}
header {
    /*font-family: arial;*/
    
    font-size: 14px;
}
.smallscreen #avatar,
.smallscreen #friendlist,
.smallscreen #stagewrapper {
    top: 33px;
}
.chat #stagewrapper,
.chat.chatoverlay #stagewrapper,
.chat.chatoverlay.chatlarge #stagewrapper {
    bottom: 36px;
}
#stagewrapper {
    top: 0;
    bottom: 0;
}
.chat #chat {
    bottom: 0px;
}
}
