style.css:
@-moz-document domain("rubyonrails.org") {
body {
    background: #333;
}
p {
    color: #E2E2E2;
}
#container {
    color: white;
    background-color: #333;
}
#subCol {
    background: #952f24;
    color: black;
}
#subCol .chapters {
    color: inherit;
}
#mainCol a,
#subCol a,
#feature a {
    color: inherit;
}
#mainCol a code,
#subCol a code,
#feature a code {
    color: lightgray;
}
.note {
    background: #AD9B39 url(http://guides.rubyonrails.org//images/tab_note.gif) no-repeat left top;
}
.info {
    background: #718896 url(http://guides.rubyonrails.org//images/tab_info.gif) no-repeat left top;
}
div.code_container {
    background: #6D6666 url(http://guides.rubyonrails.org//images/tab_grey.gif) no-repeat left top;
}
pre,
code {
    color: #3D8807;
}
table {
    background: transparent;
}
table th {
    background: darkgreen;
}
.description pre {
    background: #35331A;
}
}
