style.css:
@-moz-document domain("wikiwand.com"), domain("www.wikiwand.com") {
#main_menu.navbar_opaque {
	background-color: hsla(320, 4%, 32%, 1);
}
div#content {
	color: rgb(237, 237, 237);
	background-color: rgba(38, 38, 38, 1);
}
#wikiwand_toc {
	background-color: rgba(58, 58, 58, 1);
}
#main_menu.navbar_opaque {
	background-color: hsla(320, 4%, 32%, 1);
}
body:not(.dark) #header.fold #main_menu.navbar_opaque {
	background-color: rgb(84, 78, 82);
}
#read_more a {
	background-color: rgba(13, 10, 12, 1);
}
#main_menu>li.toc_folded {
	background-color: #544e52;
}
body.light #article_content_wrapper.padded,
body.light #body_wrapp {
	color: rgb(237, 237, 237);
	background-color: rgba(38, 38, 38, 1);
}
#main_menu {
	background-color: rgba(84, 78, 82, 1);
}
}
