style.css:
@charset "utf-8";
/* ----------------------------------------------------------------------------
 * OpenStreetMap no-sidebar
 * ----------------------------------------------------------------------------
 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.openstreetmap.org") {
#sidebar
{
  display:none;
}

}

@-moz-keyframes glow {
0%{ opacity:0.2 !important }
50%{opacity:1.0!important; }
100%{ opacity:0.2!important;}
}
