style.css:
@-moz-document domain(hex.pm) {
body{
background-color: #1D212A;
}

.navbar-default{
background: #303748;
border-color: #6F4893;
}

.navbar-default .navbar-nav>li>a:hover{
color: #89B650;
}

h1, h2, h3, h4, h5, h6{
color: #6690CD;
}

code{
background-color: #000000;
color: #EDB233;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #303748;
}

.form-control {
background-color: #4B5570;
color: grey;
}

.panel{
border-color: black;
}
.panel-default>.panel-heading {
    background-color: #6690CD;
}

.panel-body{
background: #4B5570;
}

.panel .form-control{
  color: white;
}

table{
  color: white;
}

.text-muted{
color: grey
}

#footer{
background-color: #303748;
}
}
