style.css:
@-moz-document domain("twitter.com") {
.dashboard,
.content-main {
    float: none;
    width: 100%;
    margin: 0 0 !important;
}
.DashboardProfileCard,
.DashboardProfileCard-bg {
    border: none;
}
.DashboardProfileCard-avatarLink.u-inlineBlock,
.DashboardProfileCard-avatarImage {
    line-height: 0px;
    border: 0;
    padding: 0;
}
.AdaptiveMedia {
    width: 100%;
    height: auto;
    max-height: none;
    max-width: none;
    margin: auto;
}
.trends,
.trends-dialog,
.dashboard-right {
    display: none;
}
}
