style.css:
@-moz-document domain("www.azlyrics.com") {
body {
	background-color: #2F2F35;
}
.main-page {
	color: #B9B9B9;
	background-color: #404048;
}
.navbar-default {
	background-color: #535373;
}
.breadcrumb.noprint, .panel.album-panel.noprint {
	display: none;
}
.navbar-footer, .footer-wrap {
	background-color: #52525F;
}
.input-group .form-control {
  background-color: #aaa;
}
.btn-menu {
	background-color: #686890;
	border-color: #707098;
}
.fb-like.fb_iframe_widget {
	display: none;
}
}
