style.css:
@-moz-document domain("www.imzy.com") {
article.comment__content.u-html.u-cur-default {
    background-color: #dbd4db;
    color: #000000;
    font-family: monospace;
}

.comments-feed.is-post-overlay .comments-feed-wrapper {
    background: #e8e5e8;
	}

div.comments-feed-wrapper {
    background-color: #e8e5e8;
}

div.feed-post-overlay__content {
    background-color: #d1d1d1;
}

div.page-default {
    background-color: #dedede;
}

div.page-default.has-no-footer {
    background-color: #e3e3e3;
}

div.page-home-feed {
    background-color: #dedede;
}

div.post__content__body.u-html.u-cur-default.js-spoilers {
    color: #000000;
}


div.post__main {
    background-color: #e8e5e8;
}

header.header.is-logged-in {
    background-color: #f0f0f0;
}

li.feed-post__content__tag.u-cur-default {
    background-color: #d4d2d4;
    color: #000000;
}

li.post__content__tag.u-cur-default {
    background-color: #d4d2d4;
    color: #000000;
}

li.profile-view__activity__comment-item {
    background-color: #f0f0f0;
}

section.feed-item__main {
    background-color: #e8e5e8;
}

ul.feed__new-post.has-items-6 {
    background-color: #f0f0f0;
}

section.post-create {
    background-color: #f0f0f0;
}

section.profile-view__activity__post-item__main {
    background-color: #f0f0f0;
}
}
