div.sidebar div.widget.latestReviewsWidgetV2 div.widgetContent div.reviews div.review.active,
div.sidebar div.widget.topCamerasWidgetV2 div.widgetContent div.cameras a.camera:hover,
div.sidebar div.widget .simpleListWithCounts tr:hover {
	background: linear-gradient(0deg,
			rgba(255, 255, 255, .4) 0,
			rgba(0, 0, 0, .6) 2%,
			rgba(0, 0, 0, .6) 98%,
			rgba(255, 255, 255, .4) 100%);
}

body.light div.sidebar div.widget.latestReviewsWidgetV2 div.widgetContent div.reviews div.review.active,
body.light div.sidebar div.widget.topCamerasWidgetV2 div.widgetContent div.cameras a.camera:hover,
body.light div.sidebar div.widget .simpleListWithCounts tr:hover {
	background: linear-gradient(0deg,
			rgba(0, 0, 0, .4) 0,
			rgba(255, 255, 255, .6) 2%,
			rgba(255, 255, 255, .6) 98%,
			rgba(0, 0, 0, .4) 100%);
}

body.light div.mainContent div.keyboardShortcuts div.shortcuts span.keyLabel span.key,
div.mainContent div.keyboardShortcuts div.shortcuts span.keyLabel {
	width: auto;
	padding: 0 .3em;
}

div.mainContent div#amazonBuyboxContainer.singleProductMultipleVariations div.buybox-new div.body div.variationsBlock {
	width: calc(100% - 150px);
}

@media screen and (min-width: 900pt) {

	#featureMarquee,
	table.images.singleNoBorder,
	table.images.twoRight td.image,
	table.images.twoBottom td.image,
	div.categories div.category div.article div.image a,
	.mainContent .horizontalArticlesList td.articleImage {
		zoom: 1.25
	}
}

@media screen and (min-width: 1200pt) {

	#featureMarquee,
	table.images.singleNoBorder,
	table.images.twoRight td.image,
	table.images.twoBottom td.image,
	div.categories div.category div.article div.image a,
	.mainContent .horizontalArticlesList td.articleImage {
		zoom: 1.6
	}
}
}
