style.css:
@-moz-document domain("share.dmhy.org"), domain("dmhy.anoneko.com")
{
	#bebiv3_ad,
	iframe,
	.footer,
	a[href="http://www.pixivbook.com/"],
	.ad,
	[class^="kiwi-ad-"],
	.user-sidebar .box:last-of-type,
	div.table:nth-of-type(4):nth-last-of-type(3),
	div.table:nth-of-type(5):nth-last-of-type(2),
	a[href="http://pixivbook.com/"] {
		display: none !important
	}
	
	body {
		font: 400 12pt sans-serif;
		min-width: 1130px
	}
	
	.headerleft {
		height: 82px;
		overflow: hidden;
		margin-top: -39px
	}
	
	.headerright .links,
	.headerright .links a {
		display: inline-block;
		font-size: 13pt;
		height: 30px
	}
	
	.bg {
		min-width: 900px;
		margin: 0 5mm;
	}
	
	.bg>.header {
		position: fixed;
		width: 100vw;
		height: auto;
		background: #247;
		box-shadow: 0 0 0 1px #666, 0 0 2mm #000;
		left: 0;
		top: -43px;
		z-index: 2;
		transition: top .8s .5s
	}
	
	.bg>.header:hover {
		top: 0;
		transition: top .1s .1s
	}
	
	.top_sort .menu ul {
		white-space: nowrap;
		box-shadow: 1px 3px 3mm #888;
		border-radius: 0 0 2mm 2mm
	}
	
	.top_sort .menu ul li {
		width: auto
	}
	
	.top_sort .menu ul li:last-child a {
		border-radius: 0 0 2mm 2mm
	}
	
	.top_sort .menu ul li a {
		font-size: 13pt;
		width: auto;
		min-width: 3em;
		padding: 3px 13px
	}
	
	.top_sort .menu ul li:hover ul {
		width: auto
	}
	
	.quick_search {
		padding: 2mm 4mm;
		border-radius: 10mm;
		box-shadow: inset 0 0 2mm #333
	}
	
	.main {
		background: linear-gradient(100deg,
				#bbb 0,
				#ddd 30%,
				#ddd 50%,
				#bbb 100%);
		padding: 1em;
		margin: 36px 0 0;
		border-radius: 10pt;
		box-shadow: 0 0 2mm #000
	}
	
	.main>.table.clear {
		width: auto !important;
		margin: 0
	}
	
	table.jmd tr th {
		text-align: right;
		width: 5.5em;
		white-space: nowrap
	}
	
	table.jmd tr.today {
		background: #ffd700 !important
	}
	
	table.jmd a {
		transition: all 220ms;
		display: inline-block;
		color: #FFD !important;
		background: rgba(50, 50, 80, 0.8);
		line-height: 1.2 !important;
		padding: 1px .6em 0 !important;
		border: none !important;
		border-radius: 1em;
		margin: 1px 5px 1px 0 !important
	}
	
	table.jmd tr.today a {
		background: rgba(80, 50, 30, 0.8)
	}
	
	table.jmd a:hover {
		transition: all 60ms;
		text-shadow: 0 0 1px #fff;
		box-shadow: 0 1px 6px rgba(0, 0, 0, 0.8);
		text-decoration: none !important;
		color: maroon !important;
		background: #DDD !important
	}
	
	table.jmd a:active {
		box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.6);
		color: red !important;
		background: #CCC !important
	}
	
	.nav_title {
		padding: 2px;
		line-height: 1.33;
	}
	
	.table {
		padding: 0;
		background-color: transparent
	}
	
	table.tablesorter {
		border-spacing: 0
	}
	
	table.tablesorter tr td:nth-child(even) {
		background-color: rgba(120, 120, 120, .1) !important
	}
	
	table.tablesorter tr:nth-child(odd) {
		background-color: rgba(180, 200, 180, .5)
	}
	
	table.tablesorter tr:nth-child(even) {
		background-color: rgba(180, 180, 200, .5)
	}
	
	table.tablesorter tr:hover {
		background: rgba(200, 220, 230, .5) linear-gradient(0deg,
				rgba(100, 120, 130, .5) 0,
				rgba(245, 245, 255, .3) 4px,
				rgba(245, 245, 255, .3) calc(100% - 5px),
				rgba(100, 120, 130, .5) 100%);
	}
	
	table.tablesorter th:nth-child(1),
	table.tablesorter td:nth-child(1) {
		width: 5.5em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(2),
	table.tablesorter td:nth-child(2),
	table.tablesorter th:nth-child(4),
	table.tablesorter td:nth-child(4) {
		width: 2.1em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(5),
	table.tablesorter td:nth-child(5) {
		width: 4.5em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(6),
	table.tablesorter td:nth-child(6),
	table.tablesorter th:nth-child(7),
	table.tablesorter td:nth-child(7),
	table.tablesorter th:nth-child(8),
	table.tablesorter td:nth-child(8) {
		width: 2.1em !important;
		white-space: nowrap;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(9),
	table.tablesorter td:nth-child(9) {
		width: 6em !important;
	}
	
	table.tablesorter th,
	table.tablesorter td:nth-child(1),
	table.tablesorter td:nth-child(2),
	table.tablesorter td:nth-child(n+5) {
		font-size: .92rem;
	}
	
	table.tablesorter tbody td {
		line-height: 1.25;
		padding: 0 2px;
		background: transparent linear-gradient(180deg,
				#aaa 0,
				transparent 1px,
				transparent 100%) !important
	}
	
	table.tablesorter td.title {
		padding-left: 5px;
	}
	
	table.tablesorter td.title .tag {
		float: left;
		height: auto;
		line-height: 1.4;
		background-color: #faf0d3 !important;
		padding: 0 1mm !important;
		margin: .6em 2mm .6em 0;
		border-radius: 6px;
	}
	
	table.tablesorter td.title .tag:hover {
		background-color: #d3fade !important;
		border-color: #800 !important;
		box-shadow: 0 1px 5px #888;
	}
	
	table.tablesorter td.title {
		position: relative;
	}
	
	table.tablesorter td.title span[style="color: gray;"] {
		position: absolute;
		display: block;
		right: 1mm;
		bottom: 1px;
		font-size: 8pt;
		letter-spacing: 1px;
		padding: 0 2mm;
		border-radius: 1em;
		color: #555 !important;
		background: rgba(245, 215, 220, .6);
		box-shadow: inset 0 0 0 1px #999;
		z-index: 0;
	}
	
	table.tablesorter td:last-child>a {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	table.tablesorter .title>a {
		position: relative;
		color: #334;
		padding: 1px 0;
		height: 2.6em;
		line-height: 2.6;
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-shadow: 0 0 2px #ddd;
		z-index: 1;
	}
	
	table.tablesorter .title>a:hover {
		color: #a00;
	}
	
	table.tablesorter .title>a:active {
		color: #620
	}
	
	table.tablesorter a:hover {
		border: none
	}
	
	table.tablesorter b {
		font-weight: normal;
	}
	
	.table a.sort-7 {
		font-size: 0;
	}
	
	.table a.sort-7:after {
		display: inline;
		font-size: .9rem;
		content: "RAW";
	}
	
	.arrow-magnet {
		display: block;
		width: 100%;
		font-size: 0;
		background: none;
		padding: .2rem 0 0;
		margin: 0 auto;
	}
	
	.arrow-magnet:after {
		font-size: 24px;
		line-height: 2.4rem;
		content: "📥";
		color: #263;
	}
	
	.topic-main {
		position: relative
	}
	
	.topic-title h3,
	.topic-main #resource-tabs p {
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
		margin: 0 0 .1em
	}
	
	a[name="description-end"] {
		position: absolute;
		top: 0;
	}
	
	.topic-main #resource-tabs {
		position: absolute;
		top: 3.3em;
		left: 1em;
	}
	
	.topic-main #resource-tabs {
		width: calc(100% - 300px);
		max-height: 160px;
		padding: 2mm;
		margin: 0;
		overflow: hidden;
		background: rgba(255, 255, 255, 0.7);
		box-shadow: 0 1px 2mm #666;
		transition: max-height .5s .3s
	}
	
	.topic-main #resource-tabs:hover {
		max-height: 60vh;
		overflow: scroll;
		transition: max-height .3s
	}
	
	#resource-tabs .ui-tabs-nav,
	#resource-tabs hr {
		display: none
	}
	
	#resource-tabs .file_list {
		width: auto !important;
		max-height: 100% !important;
		margin: 0 !important;
		z-index: -99
	}
	
	.ui-tabs .ui-tabs-panel {
		padding: 0 !important;
		margin: 0 !important
	}
	
	#preview {
		border: none
	}
	
	#preview>img {
		object-fit: contain;
		max-width: 90vw;
		max-height: 90vh;
		background: #fff;
		padding: 2.5mm;
		border-radius: 2.3mm;
		box-shadow: 0 1.5mm 15mm 1mm rgba(0, 0, 0, .8)
	}
	
	img[alt="rss"] {
		display: inline-block;
		margin: 0 0 4px
	}
	
	.ui-widget {
		font-size: 11pt;
	}
	
	#mini_jmd {
		position: relative;
		max-height: 4em;
		overflow: hidden;
		transition: .3s;
	}
	
	#mini_jmd:after {
		content: "";
		display: block;
		position: absolute;
		bottom: 0;
		width: 100%;
		height: 2em;
		max-height: 2em;
		background: linear-gradient(0deg, #333 0, transparent 90%);
		transition: .7s;
	}
	
	#mini_jmd:hover {
		max-height: 15em;
		overflow: auto;
	}
	
	#mini_jmd:hover:after {
		max-height: 0;
	}
	
	.file_list li {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	.topic-main #resource-tabs p {
		text-align: left;
	}
	
	.even {
		background: #e8eafc;
	}
	
	.topic-main img {
		max-width: 100%;
		height: auto !important;
	}
}

@-moz-document domain("bt.acgzero.com")
{
	.footer {
		display: none !important;
	}
	
	body {
		font: 12pt sans-serif;
		background: #bbb;
		padding: 0 0 12mm;
	}
	
	a {
		text-decoration: none !important;
	}
	
	.header {
		box-shadow: 0 0 2mm #666;
		margin: 0 0 -2mm;
	}
	
	.container {
		width: calc(100% - 15mm);
		max-width: 100%;
		padding: 0;
	}
	
	.breadcrumb {
		background: #f4f4f4 !important;
		padding: 2mm;
		margin-bottom: 2mm;
		border-radius: 1.5mm;
		line-height: 1;
		box-shadow: 0 0 0 1px rgba(50, 50, 50, .2);
	}
	
	.table th {
		border: none !important;
		padding: 0;
		line-height: 1.8;
		text-align: center
	}
	
	.table td {
		padding: 0 1.5mm;
		vertical-align: middle;
		border: none;
	}
	
	.card,
	.list-table {
		background: #f4f4f4;
		border-radius: 1.5mm;
		overflow: hidden;
		box-shadow: 0 0 0 1px rgba(50, 50, 50, .2);
	}
	
	.list-table td a {
		padding: 0;
		height: 2.3em;
		line-height: 2.3;
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.table-striped td:nth-child(odd) {
		background: rgba(150, 150, 200, .1)
	}
	
	.table-striped tbody tr:nth-of-type(odd) {
		background: linear-gradient(180deg,
				#888 0,
				rgba(220, 220, 170, .3) 1px);
	}
	
	.table-striped tbody tr:nth-of-type(even) {
		background: linear-gradient(180deg,
				#888 0,
				rgba(190, 220, 220, .3) 1px);
	}
	
	.table-striped tbody tr:hover {
		background: linear-gradient(180deg,
				#888 0,
				rgba(160, 160, 230, .25) 1px);
	}
	
	.list-table td:nth-child(1),
	.list-table td:nth-child(3),
	.list-table td:nth-child(n+5) {
		text-align: center;
	}
	
	.list-table td:nth-child(4) {
		text-align: right;
	}
	
	.author {
		min-width: 6em;
	}
	
	.list-cat-td,
	.list-dl-td {
		text-align: center;
		min-width: 3em;
	}
	
	.ml-2,
	.mx-2 {
		padding: .3em .5em;
	}
	
	ul.pagination {
		position: fixed;
		bottom: 0;
		right: 30mm;
		margin: 0 0 4mm;
		box-shadow: 0 0 0 1px #aaa;
		transition: .2s;
	}
	
	ul.pagination:hover {
		box-shadow: 0 1px 3mm 1px #888;
	}
	
	.card-body .content-html * {
		font-size: 12pt;
	}
	
	.card-footer {
		position: fixed;
		left: 0;
		bottom: 0;
		background: rgba(255, 255, 255, .8);
		width: 100vw;
		padding: 0 5% 1mm;
		text-align: center;
		box-shadow: 0 0 2mm #666;
		opacity: .6;
		z-index: 3;
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
		transition: .4s;
	}
	
	.card-footer:hover {
		opacity: 1;
		transition: .1s;
	}
	
	.btn,
	.form-control {
		padding: 2px 2mm;
	}
}

@-moz-document domain("acg.rip")
{
	#bangumi-box,
	.pagination:not(.top-paginate) {
		display: none;
	}
	
	body {
		font-family: sans-serif;
		font-size: calc(8pt + .4vw);
		background: #bbb;
	}
	
	.post-index .date,
	.post-index .date a,
	.post-index .size,
	.post-index .peers {
		font-size: calc(8pt + .25vw);
	}
	a:hover {
		text-decoration: none;
	}
	
	.navbar,
	.well,
	.breadcrumb {
		margin-bottom: 3mm;
	}
	
	.container {
		width: calc(100vw - 15mm);
		max-width: 1600pt;
	}
	
	#bangumi-box {
		background: #eee;
	}
	
	.well {
		padding: 1mm 2mm;
	}
	
	.label-bangumi {
		line-height: 1.4;
		display: inline-block;
		max-width: 15em;
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
		background: #bde;
		margin: 1mm 0;
		padding: 0 2mm;
		border-radius: 1em;
		box-shadow: 0 0 0 1px #8ab;
		transition: .1s;
	}
	
	.label-bangumi:hover {
		color: #622;
		background: #cef;
	}
	
	.table-condensed>thead>tr>th,
	.table-condensed>thead>tr>td,
	.table-condensed>tbody>tr>th,
	.table-condensed>tbody>tr>td,
	.table-condensed>tfoot>tr>th,
	.table-condensed>tfoot>tr>td {
		padding: 0;
	}
	
	.post-index {
		overflow: hidden;
		border-radius: 2mm;
		box-shadow: 0 0 2px #888;
	}
	
	.post-index td {
		overflow: hidden;
	}
	
	.post-index td:nth-child(odd) {
		background: rgba(170, 170, 170, .1)
	}
	
	.post-index tbody tr:nth-of-type(odd) {
		background: linear-gradient(0deg,
				#aaa 0,
				rgba(220, 220, 170, .3) 1px);
	}
	
	.post-index tbody tr:nth-of-type(even) {
		background: linear-gradient(0deg,
				#aaa 0,
				rgba(190, 220, 220, .3) 1px);
	}
	
	.post-index tbody tr:hover {
		background: linear-gradient(0deg,
				#aaa 0,
				rgba(160, 160, 230, .25) 1px);
	}
	
	.post-index .date {
		color: #700;
	}
	
	.post-index .date a {
		max-width: 8em;
		color: #032;
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.post-index td.title {
		padding: 1px 0 0 5px;
	}
	
	.post-index .label {
		float: left;
		line-height: 1;
		margin: .5em 1mm 0 0;
		padding: 0;
		border: none;
		box-shadow: 0 0 0 1px #66a;
	}
	
	.post-index .label:hover {
		background: #faf2cd;
	}
	
	.post-index .label a {
		display: block;
		padding: .25em .3em .2em;
	}
	
	.post-index span.title a,
	.post-index .action a {
		height: 2.5em;
		line-height: 2.5;
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.post-index a {
		text-shadow: 0 0 1px #bbb;
	}
	
	.post-index a:hover {
		color: #00a;
	}
	
	.post-index .size {
		width: 5em;
		text-align: right;
		padding: 0 1mm 0 0;
		white-space: nowrap;
	}
	.post-index .peers {
		width: 6em;
		line-height: 1.2;
	}
	.post-index .peers .row {
		white-space: nowrap;
	}
	
	.post-index .peers .seed,
	.post-index .peers .leech {
		display: inline;
		float: none;
		margin: 0;
	}
	
	.post-index .peers .seed:before {
		display: inline;
		content: "⬆︎";
		color: #082;
	}
	
	.post-index .peers .leech:after {
		display: inline;
		content: "⬇︎";
		color: #00c;
	}
	
	.post-index .peers .done:before {
		display: inline;
		content: "🙂";
		color: #862;
	}
	
	.pagination.top-paginate {
		position: fixed;
		bottom: 5mm;
		right: 30mm;
		background: rgba(255, 255, 255, .6);
		margin: 0;
		padding: 2mm 3mm;
		text-align: center;
		z-index: 2;
		opacity: .8;
		box-shadow: 0 0 1mm #666;
		transition: .2s;
		backdrop-filter: blur(1mm);
		-webkit-backdrop-filter: blur(1mm);
	}
	
	.pagination.top-paginate:hover {
		opacity: 1;
		box-shadow: 0 2px 3mm rgba(0, 0, 0, .7);
	}
	
	.pagination>li>a,
	.pagination>li>span {
		display: inline-block;
		line-height: 1.8;
		height: 1.8em;
		min-width: 8mm;
		text-align: center;
		padding: 0 2mm;
		margin: 0 1px 0 0;
		border: none;
		box-shadow: 0 0 0 1px #999;
	}
	
	.pull-right {
		background: #eee;
		border-radius: 5px;
	}
}

@-moz-document domain("bangumi.moe")
{
	div#main {
		width: calc(100% - 25mm);
	}
	
	md-tab,
	.bangumi-list {
		padding: 0;
	}
	
	.bangumi-list-item {
		margin: 2mm;
	}
	
	.compact-torrent-list .torrent-row .torrent-title h3 {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
		z-index: 1;
	}
	
	.compact-torrent-list .torrent-row .torrent-publish-info {
		max-height: none;
		font-size: 9pt;
		line-height: 1.4;
		padding: 0 2mm;
		margin-right: 2px;
		background: rgba(0, 0, 0, .4);
		opacity: 0;
		z-index: 0;
	}
	
	.compact-torrent-list .torrent-row:hover .torrent-publish-info {
		opacity: .8;
		transition: .4s 1s;
	}
	
	.compact-torrent-list .torrent-row md-item-content {
		transition: .2s;
	}
	
	.compact-torrent-list .torrent-row md-item-content:hover {
		transition: 0s;
	}
	
	.torrent-row:nth-of-type(odd) .md-item-simple {
		background: #e8f4e7;
	}
	
	.torrent-row:nth-of-type(even) .md-item-simple {
		background: #e8e7f4;
	}
	
	.torrent-row .md-item-simple:hover {
		background: #fafafa linear-gradient(0deg,
				#bbc 0,
				#fafafa 4px,
				#fafafa calc(100% - 4px),
				#bbc 100%);
	}
	
	.compact-torrent-list .torrent-row .md-tile-content {
		padding: .5em .3em;
	}
	
