style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
/*[[guard]]*/
/*                   */
/*  color settings   */
/* (-'17 old design) */
/*                   */

.resume-playback-progress-bar{
	background: /*[[color]]*/ !important;
}

/*                   */
/*  color settings   */
/* ('18- new design) */
/*                   */

#progress.ytd-thumbnail-overlay-resume-playback-renderer{
	background-color: /*[[color]]*/ !important;
}

/*[[separate]]*/
}

options.value:
/*                   */
/*  guard settings   */
/* (-'17 old design) */
/*                   */

.resume-playback-background{
	display:none !important;
}
.resume-playback-progress-bar{
	display:none !important;
}

/*                   */
/*  guard settings   */
/* ('18- new design) */
/*                   */

ytd-thumbnail-overlay-resume-playback-renderer{
	display:none !important;
}

/*                   */
/* separate settings */
/* (-'17 old design) */
/*                   */

.resume-playback-background{
	background: #fff !important;
	opacity: 1 !important;
	height: 5px !important;	/* aka + shiro no takasa wo shitei */
}
.resume-playback-progress-bar{
	height: 3px !important;	/* aka no takasa wo shitei */
}

/*                   */
/* separate settings */
/* ('18- new design) */
/*                   */

ytd-thumbnail-overlay-resume-playback-renderer{
	background: var(--yt-app-background) !important;
	height: 5px !important;	/* aka + blank no takasa wo shitei */
}
#progress.ytd-thumbnail-overlay-resume-playback-renderer{
	height: 3px !important;	/* aka no takasa wo shitei */
	margin-top: 2px !important;	/* blank no takasa wo shitei */
}
