style.css:
 @-moz-document domain(444.hu) {
     body {
         background-color: #252525;
         color: #eee;
     }

     h1, h2, h4, .h2, .h3 {
         color: #ddd;
     }

     .highlight-yellow {
         color: #000;
     }

     .widget-recommendation a, li {
         color: #eee;
     }

     .subhead::after {
         background-color: #fff;
     }
     
     .subhead {
         color: #fff;
     }

     .post-byline::before, .report-byline::before, .tag-list::before, .widget-stream-item .widget-stream-byline::before, .widget-stream-byline::before, .embedly-card iframe {
         background-color: #252525 !important;
     }

     figure figcaption {
         background-color: #000;
     }
 }
