style.css:
@-moz-document domain("btgigs.info") {
body {
    margin-left: 15px;
    margin-right: 15px;
    background-color: #252525;
}
table.main,
table.mainouter {
    background-color: #252525;
}
table {
    border-collapse: collapse;
    border: none;
    background-color: #252525;
}
a:link {
    text-decoration: inherit;
    color: #858585;
}
a:visited {
    color: #555;
}
a.altlink:link,
a.altlink:visited {
    color: #ffffff;
}
td {
    font-size: 8pt;
    border: solid #999 1px;
    color: #ccc;
    background-color: #252525
}
td.text {
    padding: 10pt;
    text-align: left;
    background-color: #252525;
}
tr {
    display: table-row;
    vertical-align: inherit;
    border-color: inherit;
    color: black;
    background-color: #252525;
}
table.bottom {
    background: #252525;
    color: white;
    box-shadow: 3px 3px 3px 3px black;
}
input,
select,
textarea {
    font-family: "tahoma", "arial", "helvetica", "sans-serif";
    font-size: 8pt;
    color: #ccc;
    background-color: #252525;
}
.pomoc,
.datki,
.wstaw{
    color: #005dff;
    text-decoration: underline;
}
tr.text {
    color: white;
}
span.smallfont {
    color: white;
}

.text {
    padding: 10pt;
    text-align: left;
    background-color: #111;
}
body {
    font-family: "tahoma", "arial", "helvetica", "sans-serif";
    font-size: 8pt;
    color: white;
    margin-top: 0px;
}
a:hover {
    color: #fff;
}
}
