style.css:
@-moz-document domain("listography.com") {
body.list div.listbox div.text_content {
    margin: 0 10px;
}
.font_Helvetica {
    font-family: consolas;
    font-size: 12px;
    text-align:justify;
}
.listbox-content ul {
    margin-right: 0;
}
ul {
    margin: 5px 0 0 -25px;
}
p {
    text-align: center!important;
}
strong {
    font-size: 20px;
    text-decoration: underline;
}
body.list div.center_container {
    margin: 41px auto;
    width: 625px;
}
}
