style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("exploringjs.com") {
    body {
        background-color: #002b36;
        color: #839496;
    }
    h1, h2, h3, h4, h5, h6 {
        color: #b58900;
    }
    a, a:link {
        color: #268bd2;
    }
    a:visited, a:hover {
        color: #6c71c4;
    }
    ol.toc a:link, ol.toc a:active, ol.toc a:visited {
        color: #268bd2;
    }
    :target,
    div.footnotes p:target + p {
        background-color: #073642;
    }
    #top-bar {
        background-color: #073642;
    }
    aside.blurb {
        background-color: #002b36;
    }
}
