style.css:
@-moz-document domain("dailymotion.com") {
body,
body a,
.sd_footer a {
    color: grey !important;
}
.sd_header__upload,
a.btn_v2>span,
a.btn_v2>i,
.btn-primary {
    color: white !important;
}
ul.mo_tabs {
    border-bottom-width: 0px !important;
}
.mo_tabs>li .tab-content {
    font-weight: bold;
}
a.link-on-hvr,
a.link,
.foreground {
    color: rgb(103, 131, 171) !important;
}
#topwrapper,
.page-channel .main-content {
    background-color: #101010;
}
#wrapper #content,
.navigation-panel,
.sd_footer,
#videos>div:last-child>div>div:last-child,
#popular>div:last-child>div>div:last-child, 
#live>div:last-child>div>div:last-child, 
#likes>div:last-child>div>div:last-child,
#premium>div:last-child>div>div:last-child {
    background-color: #1E1E1E !important;
    border: 0;
}
li.ellipsis,
.sd_footer,
.sd_footer__credits>span,
.sd_footer__follow_us div,
.sd_footer__follow_us>a {
    color: white !important;
}
}
