/* AD REMOVE & ETC */
.sidebar, div[id*="google_ads_iframe"]/*[[NextArtLoader]]*//*[[Tags]]*//*[[BigPosts]]*/ {
	display:none!important;
}

/* FIXES AFTER AD REMOVE & ETC */
.posts {
    width: 100%!important;
}
.post-content {
    width: 100%!important;
    margin-left: 0!important;
}
@media (min-width: 1800px) {
	.container {
    	max-width: 1200px!important;
	}
}

/*[[ShareButtons]]*/

/* DISQUS FIX */
#disqus_thread {
    width: 100%!important;
    z-index: 0!important;
}
.post-content iframe {
    width: 100%!important;
    margin-left: 0!important;
}

/* FOOTER COPYRIGHT REMOVE */
footer p {
    display: none!important;
}
footer img {
    margin-bottom: 30px!important;
}
footer {
    padding: 100px 0 50px 0!important;
    height: 255px!important;
}
.page-content {
    margin-bottom: 200px!important;
}

/*[[StickyNavbar]]*/
}

options.value:
/* TOP NAVBAR ABSOLUTE */
nav {
    position: absolute!important;
    transition: none!important;
    -webkit-transition: none!important;
}
nav.hidden {
    top: 0!important;
}

/* SHARE TOP BUTTONS REMOVE */
.share-btn.facebook:nth-of-type(3n+1), .share-btn.twitter:nth-of-type(3n+2) {
 display: none!important;   
}

/* SHARE BOTTOM BUTTONS REMOVE */
.share-btn.facebook:nth-of-type(3n+0), .share-btn.twitter:nth-of-type(3n+1) {
 display: none!important;   
}

/* SHARE ALL BUTTONS REMOVE */
.share-btn.facebook, .share-btn.twitter {
 display: none!important;   
}
