style.css:
@-moz-document url-prefix("https://userstyles.org/") {
body, #nav-view, #footer, #navigation, .edit-setting, .option-user-url, .text_setting_input, #nav-view-responsive, .navigation .links-box .navigation-link:hover {
    background-color: #202020 !important;
}
.navigation_link:hover, #notes_textarea, #header, #stylish-code, .style_card, .div-arrow, input#style_short_description, textarea#style_long_description, textarea#style_additional_info, textarea#css, input#example-url, input#style_pledgie_id, .white_button.no-select, #send_feedback_button, #css_button, #archive_style_button, .see_more, .style_category_card_wrapper, #edit-style-settings textarea, #edit-style-settings input:not([type]), .edit-option-value-url, .css_button, .advanced_button, .advancedsettings_shown, #install_style_button, #install_style_button:hover, #overlay-info, .navigation, .us-stylecard--short, .us-stylecard--long {
    background-color: #161616 !important;
}
#notes_textarea, #stylish-code, .div-arrow, input#style_short_description, input#style_pledgie_id, textarea#style_long_description, textarea#style_additional_info, textarea#css, input#example-url, .white_button.no-select, #send_feedback_button, #css_button, #archive_style_button, .see_more, #edit-style-settings textarea, #edit-style-settings input:not([type]), .edit-option-value-url, .css_button, .advanced_button, .advancedsettings_shown, .option-user-url, .text_setting_input, #install_style_button {
    border: 1px solid #000 !important;
}
#archived_title {
    color: #e74c3c !important;
    font-weight: bolder;
}
#style_installed_button {
    background-color: #161616 !important;
}
.screenshot_wrapper {
    border-bottom: 1px solid #000 !important;
}
.style_title, #notes_textarea, .text_style_page, #copyrights, #headline, #information_key_left, #infomation_value_left, #information_value, #information_key, #share_div, #stylish-code, .navigation_title, .pageNumber, input#style_short_description, input#example-url, textarea#style_long_description, textarea#style_additional_info, textarea#css, h4 a, h1, li, label, #title_long a, .white_button no-select, .white_button, #edit-style-settings textarea, #edit-style-settings input:not([type]), .edit-option-value-url, dl, h2, tr, p, .option-user-url, .text_setting_input, .linking-note, div.form-controls, .title_setting, #applies_link, #archived, .navigation .links-box .navigation-link .navigation-title {
    color: #CCC !important;
}
h4 a:visited, #title_long a:visited {
    color: #48698a !important;
}
#update_style_button {
    color: #f5f7fa !important;
}
.linksBox .navigation_link:last-child, #single_discussion, .line_seperator, .style_card_long, .navigation .links-box .navigation-link, .navigation .links-box {
    box-shadow: 0 1px 0 0 #000000, 0 0 0 0 #000000 !important;
}
#archive_style_button, #install_style_button {
    box-shadow: 0 6px 16px 0 rgba(80, 178, 243, 0) !important;
}
.style-warning {
    background-color: #f00 !important;
    color: #000 !important;
}
.errorExplanation {
    background-color: #de2f2f !important;
}
.style_author_long {
    color: #466584 !important;
}
img.logo-navigation-link {
    filter: grayscale(100%) brightness(4);
}
#copyrights:after {
    content: " Dark Userstyles v2.0";
}
}
