style.css:
@-moz-document url-prefix("http://www.volkerbehrendpeters.com")
{
div#container {
    margin: 0 5vw 0 auto;
    width: 970px;
}
.left img {
    float: right;
    width: 74vw;
    max-width: 1200px;
    height: 100%;
    min-width: 736px;
}
.left .gallery_img_card a img {
    width: auto;
    max-width: auto;
    height: auto;
    min-width: auto;
}
div#header,
div#footer {
    margin-left: 20px;
}
div#header div#submenu {
    margin-left: 0;
}
}
