style.css:
@-moz-document domain("republica.ro") {

body,
html,
.navbar-light,
.navbar-fixed-bottom-in-view,
.container-fluid,
.btn-secondary,
.chat-area,
.messages,
.chat-bubble,
.card-articles .card-green-light {
    background-color: hsl(0, 0%, 30%)!important;
    color: hsl(0, 0%, 90%)!important;
}

a {
    color: hsl(0, 0%, 80%)!important; 
}

a:hover {
    color: hsl(0, 0%, 60%)!important; 
}

.card {
    border-bottom: 1px solid hsl(0, 0%, 25%)!important;
}


.inputMessage {
    background-color: hsl(0, 0%, 70%)!important;
    color: hsl(0, 0%, 100%)!important;
    border: none !important;
}


}
