style.css:
@-moz-document domain("vue-loader.vuejs.org") {
body {
	font-size: 1.7em;
}
.book,
.book-summary,
.book-body,
#book-search-input {
	background-color: #2D2D2D !important;
}
.book-summary .summary .book-summary a {
	color: #6B7879;
}
.book-summary .summary li.chapter span {
	color: #FF8100 !important;
	font-weight: bold;
	/* 	font-size: 1.1em; */
}
.book-body .book-inner .book-header .btn {
	color: #34B076 !important;
}
.book-body .body-inner .page-wrapper p {
	color: #4A7099 !important;
}
p code {
	background-color: #3C3C3C !important;
	color: #FF8100 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
	color: #34B076 !important;
}
h1 code,
h2 code,
h3 code,
h4 code,
p code,
li code,
strong {
	background-color: #353535 !important;
	color: #DC616A !important;
}
blockquote,
blockquote p {
	color: #FF8100 !important;
}
pre {
	background-color: #3C3C3C !important;
	color: #738182 !important;
}
pre .keyword,
pre .keyword {
	color: hsl(27, 100%, 46%) !important;
}
pre .function .keyword {
	color: hsl(200, 100%, 43%) !important;
}
pre code.lang-html .hljs-tag {
	color: #738182 !important;
}
pre code.lang-html .hljs-tag .hljs-name {
	color: #DC616A !important;
}
strong {
	font-weight: bold !important;
	padding: 0 0.25em !important;
	border-radius: 0.1em !important;
}
.markdown-section {
	color: #8DBB6E !important;
}
}
