style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bulbagarden.net") {

/* Ad & cruft removal */
#bottomframe {display:none!important;}
#extadcontainer {display:none!important;}
#rightcolbox {display:none!important;}
#inhouseadcontainer {display:none!important;}
#headeradinhouse {display:none!important;}
#amazon-related {display:none!important;}
.ad-placement, .ad-tag, #headerad {display:none!important;}
#bottomframelower {display:none!important;}
#upper-article {display:none!important;}
div.ub-emb-container {display:none!important;}
#leaderboard_atf {display:none;}
div#mw-content-text~div.visualClear+div {display:none;}
div#cdm-zone-end {display:none;}
  /* Fix the content area */
#column-content, #content, #outercontentbox, #bodyContent {min-height:0px!important;}
#globalWrapper {float:right!important;width:85%!important;overflow:visible;box-sizing:border-box!important;}
body.page-Main_Page #globalWrapper {float:initial!important;width:initial!important;overflow:initial;}
#globalWrapper header {position:absolute;width:117.62%;left:-17.62%;}
body.page-Main_Page #globalWrapper header {display:initial;position:initial;left:initial;}
#column-content {padding-right:15px!important;box-sizing:border-box!important;margin-top:77px!important;}
body.page-Main_Page #column-content {float:right!important;width:85%!important;overflow:visible;margin-top:initial!important;}
#content {max-width:100%!important;width:100%!important;float:left;border-top-right-radius:0px!important;border-bottom-right-radius:0px!important;border-right-width:0px!important;padding-right:0px!important;}
#contentbox {border-right-width:0px!important;}
html body {margin-top:0px!important;}
  /* Fix the menu */
#column-one {width:13%!important;box-sizing:border-box!important;display:block!important;visibility:visible!important;position:static!important;float:left!important;margin-left:16px!important;background-color:transparent;margin-top:66px;padding-top:50px!important;}
body.page-Main_Page #column-one {margin-top:39px;padding-top:0px!important;}
#column-one ::before {display:none!important;}
#column-one > div {margin-bottom:5px;border-style:solid;border-width:1px;border-color:#008800;background-color:#d8ffd8!important;border-radius:7px;width:100%!important;}
#column-one > div#p-personal {margin-bottom:initial!important;border-style:initial!important;border-width:initial!important;border-color:initial!important;background-color:initial!important;border-radius:initial!important;width:100%!important;}
#column-one > div div {width:100%;margin:0px;padding:0px;border-width:0px;}
#column-one > div#p-personal div {width:initial;margin-right:250px;padding:initial;border-width:initial;}
#column-one > div ul {background-color:white;border-bottom-left-radius:7px;border-bottom-right-radius:7px;box-sizing:border-box!important;width:100%!important;padding:0px;margin:0px;list-style:none!important;}
#column-one > div#p-personal ul {background-color:initial;border-bottom-left-radius:initial;border-bottom-right-radius:initial;box-sizing:initial!important;width:initial!important;padding:initial;margin:initial;list-style:none!important;}
#column-one div li {height:14pt;width:100%!important;padding-left:10px;}
#column-one div#p-personal li {height:initial;width:initial!important;padding-left:initial;}
#column-one div li:hover {background-color:#eeffee;}
#column-one div#p-personal li:hover {background-color:initial;}
#column-one div li:first-child {padding-top:5px;}
#column-one div#p-personal li:first-child {padding-top:initial;}
#column-one div li:last-child {padding-bottom:5px;}
#column-one div#p-personal li:last-child {padding-bottom:initial;background-image:none;}
#column-one div li:last-child:hover {border-bottom-left-radius:7px;border-bottom-right-radius:7px;}
#column-one div#p-personal li:last-child:hover {border-bottom-left-radius:initial;border-bottom-right-radius:initial;}
#column-one div li a {font-size:9pt;vertical-align:middle;}
#column-one div#p-personal li a {font-size:10pt;vertical-align:bottom;border-radius:5px;padding-left:5px;padding-right:5px;color:white;}
#column-one div#p-personal li a:hover {background-color:rgba(200,255,200,0.2)}
#column-one > div h3 {color:#006600;font-size:11pt;margin-left:7px;font-variant:small-caps;}

}
