style.css:
@-moz-document domain(developer.mozilla.org) {
    #main-header>div.center {
        max-width: 100%;
        background: #333;
        color: #f8f8f8;
        border-width: 0 0 10px;
    }
    .home-masthead{
        margin-top: 1px;
    }
    html:not(.no-js) .nav-main-item>a {
        color: #f8f8f8;
    }
    .nav-main-item>a:hover{
        background: #333;
        color: #f8f8f8 !important;
    }
    .submenu {
        border: none;
        background: #2e2e2e;
    }
    .submenu>.submenu-column>ul>li>a {
        color: #f8f8f8;
    }
    .logo {
        font-size: 1.5em;
        color: #f8f8f8;
        line-height: 48px;
        text-indent: 0px;
        background-image: none;
    }
    .logo:hover, logo:active {
        text-decoration: none;
        color: #ccc;
    }
}
