style.css:
@-moz-document domain("youtube.com") {
html:not([style-scope]):not(.style-scope):not([color-exp]) *, html[color-exp]:not([style-scope]):not(.style-scope) {
   
	--solarized-base03:    #002b36;
	--solarized-base02:    #073642;
	--solarized-base01:    #586e75;
	--solarized-base00:    #657b83;
	--solarized-base0:     #839496;
	--solarized-base1:     #93a1a1;
	--solarized-base2:     #eee8d5;
	--solarized-base3:     #fdf6e3;
	--solarized-yellow:    #b58900;
	--solarized-orange:    #cb4b16;
	--solarized-red:       #dc322f;
	--solarized-magenta:   #d33682;
	--solarized-violet:    #6c71c4;
	--solarized-blue:      #268bd2;
	--solarized-cyan:      #2aa198;
	--solarized-green:     #859900;

    /* Backgrounds */
    
    --yt-channel-header-background:  var(--solarized-base01);

    --yt-playlist-background-header: var(--solarized-base02);

    --yt-main-app-background: 		var(--solarized-base03) !important;
    --yt-app-background: 			var(--solarized-base03) !important;
    --yt-main-app-background-tmp: 	var(--solarized-base03);
    --yt-swatch-primary: 			var(--solarized-base03);
    --yt-guide-background: 			var(--solarized-base03);
    --yt-button-background-color: 	var(--solarized-base03);
    --yt-dialog-background: 		var(--solarized-base03);
    
    --yt-spec-general-background-a: var(---solarized-base01);
    --yt-spec-general-background-b: var(---solarized-base01);
    
    --yt-spec-brand-background-primary:  var(--solarized-base02);
    --yt-spec-brand-background-solid:    var(--solarized-base02);

    /* Foregrounds */

    --yt-searchbox-text-color:		var(--solarized-base02) !important;
    
    --ytd-comment-text-color: 		var(--solarized-base1);
  	--yt-video-publish-date-color: 	var(--solarized-base1);
    --yt-collection-badge-color: 	var(--solarized-base1);
    --yt-placeholder-text-color: 	var(--solarized-base1);
    
    --yt-metadata-color: 			var(--solarized-base2);
    --yt-button-color: 				var(--solarized-base2);
    --yt-button-text-color: 		var(--solarized-base2);
	--yt-icon-color: 				var(--solarized-base2);
    --yt-searchbox-background: 		var(--solarized-base2);
    --yt-swatch-text: var(--solarized-base2);
    
    --yt-spec-text-primary:         var(--solarized-base3);
    --yt-primary-color: 			var(--solarized-base3);
    --yt-primary-text-color: 		var(--solarized-base3);
    --yt-secondary-text-color: 		var(--solarized-base3);
    --ytd-badge-icon-color: 		var(--solarized-base3);


    
    --yt-expand-color: 				var(--solarized-cyan);

    --yt-endpoint-color: 						var(--solarized-blue);
    --yt-endpoint-hover-color: 					var(--solarized-blue);
    --yt-searchbox-legacy-button-color: 		var(--solarized-blue);
    --yt-searchbox-legacy-button-border-color: 	var(--solarized-blue);
    --yt-swatch-logo-override:					var(--solarized-blue);
    
}

.ytd-app {
    background-color : var(--solarized-base03);
}

.ytd-masthead#container {
	background-color: var(--solarized-base02);
}

[background-color-update_], [background-color-update] {
    background-color: var(--yt-main-app-background) !important; /* set in JS by youtube */
}

#search-form #container {
    border-radius: 0px;
}

#search-form {
    margin: auto;
}

html, html[dark] {
	background-color: #073642 !important;
}
}
