style.css:
@-moz-document domain(tumblr.com), url-prefix('https://www.tumblr.com/')
{

  .logo .logo-anchor
  {
    height: 60px !important;
    width: 180px !important;
    margin: 10px 0 0 20px !important;
    display: block !important;
    background-size: auto !important;
    background-image: /*[[logochange]]*/!important;
    background-repeat: no-repeat;
    content: " " !important;
  }

  .logo .preload-container
  {
    animation: none !important;
  }

  .t-logo-container a
  {
    height: 59px !important;
    width: 180px !important;
    margin: 25px 0 0 22px !important;
    display: block !important;
    background-image: /*[[logochange]]*/!important;
    background-repeat: no-repeat;
    content: " " !important;
  }

  svg.svg-logo, .logo .png-logo, .logo .preload-container, .logo-t, .logo-tumblr
  {
    display: none !important;
  }
}
