style.css:
@-moz-document domain('opskins.com') {

  .logo {
    opacity: 0;
  }

  .navbar-inverse {
    background-color: #1ea1f7
  }

  /* Text Color */

  .navbar-inverse .navbar-nav>li>a {
    color: #000
  }

  .navbar-brand .user-info h2 {
    color: #000
  }

  /* BORDER */

  .navbar-brand .user-info {
    border-left: none;
  }

  .navbar-brand .coin-count {
    border-left: none;
  }

  #language-picker {
    border-left: none;
  }
}
