@-moz-document domain("post.smzdm.com") {
.activity-stuff-bar, #sub-head, .J_feed_hot_index, #feed-side, .feed-list-slick, #footer {
    display: none !important;
}
.z-type-red, .z-type-yellow {
	padding: 0 !important;
   line-height: 1rem !important;
	width: 1rem !important;
	height: 1rem !important;
	margin: 0 !important;
   overflow: hidden;
}
.z-feed-title a {
    font-size: 14px !important;
    line-height: 14px;
}
.primary-filter-tab, .filter-feed-wrap, #feed-main-list {
