style.css:
@-moz-document url(https://webchat.freenode.net/) {
.dynamicpanel.qwebirc-qui.middleboundpanel.lines.ircwindow {
    background: black;
    color: white;
}
.dynamicpanel.qwebirc-qui.topicboundpanel.topic.tab-invisible.colourline {
    background: black;
    color: red;
}
.nicklist.tab-invisible.dynamicpanel.qwebirc-qui.rightboundpanel > a {
    color: lawngreen;
    background-color: black;
    border-bottom: 1px dashed #444444;
    border-top: none;
}
.Xc4 + span {
    color: #252525;
    font-style: italic;
}
.linestyle1.colourline {
    color: yellow;
}
.linestyle2.colourline {
    color: cyan;
}
.tabbar {
    background-color: black;
}
a.tab.tab-unselected {
    color: dimgrey;
}
a.tab.tab-unselected.tab-hilight-activity {
    color: purple;
}
a.tab.tab-unselected.tab-hilight-speech {
    color: orange;
}
a.tab.tab-unselected.tab-hilight-us {
    color: cyan;
}
input.keyboard-input {
    background-color: black;
    color: white;
}
.dropdown-tab {
    background-color: yellow;
}
}
