style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wallhaven.cc") {
/*Favlist */
  #fav-button a.fav-button-collections-toggle, 
  #fav-button > a.add-fav,
  #fav-button .fav-button-collections:before,
  #fav-button .fav-button-collections:after{
    display:none;
  }
  #fav-button .fav-button-collections{
    margin-top:0;
    background-color: transparent;
    border:none;
    box-shadow: unset;
    opacity:1;
    position:relative;
    visibility:visible;
    top:0;
  }
}
