style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://tools.wmflabs.org/pltools/harvesttemplates/") {
  body {background:rgba(0,0,0,.9);}
  div#headline {background: black}
  div#main {
    border: gray 1px solid;
    border-radius: 5px;
    background: rgba(50,50,50,1);
    color: white;
  }
  div#inst ul {padding-left: 20px;}
  form#spec {padding-left: 1em;}
  h3 {font-size: medium; font-weight: bold; font-style: normal}
  div#inst {border: gray 1px solid;}
  input[type="number"], input[type="text"], textarea {color: white; background: rgba(30,30,30,1);}
  a {color: skyblue;}
  input#getpages {background: rgba(30,30,30,1)}
  input#getpages:hover {background: rgba(160,160,160,1); color: rgba(60,60,60,1)}
}
